	void deluserdetails()
		{
				Connection con=null;
				String url="jdbc:odbc:NitLibrary";
				Statement st=null;
				
			  try
			  {
					
					String delusr =  accountno.getText();
						   delusr =  delusr.trim();
				
					Class.forName("sun.jdbc.odbc.JdbcOdbcDriver");
					
			   		con=DriverManager.getConnection(url);
					
			   		st = con.createStatement();
					
	
				int ii=st.executeUpdate("delete * From usernpasswd Where UserId='"+delusr+"'");
				int iii=st.executeUpdate("delete * From userdata Where useridno='"+delusr+"'");
				
				if(ii>0)
				{
					accountno.setText("");
					JOptionPane.showMessageDialog((Component) null, "USER ACCOUNT"+delusr+"\n  DELETED", "SUCCESS", JOptionPane.INFORMATION_MESSAGE);
			    }
					else
					{
					//accountno.setText("");
				JOptionPane.showMessageDialog((Component) null, "THE USER DOESn't exist", "Error", JOptionPane.ERROR_MESSAGE);
						
					}
					
					
				
					
					
			  }
			  catch(SQLException ex)
			   {
			    System.out.println("Unable to access the database");
			   }
			  catch(ClassNotFoundException ex)
			   {
			    System.out.println("Class not found");
			   }
			  catch(Exception ex)
			  {
               System.out.println("Exception raised is:"+ex);
			  }
			  finally{
			  con=null;
			  }
		}

    
